	//
	// Descr: Опции функции DumpCurrent
	//
	enum {
		dumpfIgnoreTimestamp = 0x0001
	};
	int    DumpCurrent(SBuffer & rBuf, long flags, int64 * pItemsCount);
	int    UndumpCurrent(SBuffer & rBuf, int use_ta);

