	void   DrvVerToStr(SString & rS) const;
	int    DrvVerFromStr(const char * pS);

	PPID   ID;               // @id
	char   Name[48];         // @name
	char   Symb[20];         //
	PPID   CurRestBillID;    // Временный документ текущих остатков по незакрытым кассовым сессиям
