	int    GetBnkAcctData(PPID bnkAcctID, const PPBankAccount *, BnkAcctData *);
	int    GetPersonReq(PPID, PersonReq *);
	int    GetTradeLicList(PPID, RegisterArray *);
	int    GetActualTradeLic(PPID, LDATE, RegisterTbl::Rec *);
