	int32  RuleGrpID;          // CSessCrDraftParam
	int32  RuleID;             // CSessCrDraftParam
    long   Reserve;            // @anchor Заглушка для отмера "плоского" участка фильтра
	TrfrAnlzFilt * P_TaF;
	SStatFilt * P_SsF;
	CCheckFilt * P_CcF;
